import React, { useState, useEffect } from 'react';
import './Welcome.css';
import MenuForm from './MenuForm/MenuForm';

const Welcome = () => {
    const [user, setUser] = useState({ Nombres: 'Usuario', Apellidos: '', _id: '' }); // Asegúrate de incluir _id en el estado
    const [sidebarOpen, setSidebarOpen] = useState(false);

    useEffect(() => {
        const userData = JSON.parse(localStorage.getItem('user')) || {};
        setUser(userData);
    }, []);

    const toggleSidebar = () => {
        setSidebarOpen(!sidebarOpen);
    };

    return (
        <div className='welcome template d-flex justify-content-center align-items-center vh-100 bg-custom2'>
            <div className='black-bar'></div>
            <div className='menu-icon' onClick={toggleSidebar}>
                <div></div>
                <div></div>
                <div></div>
            </div>
            <div className={`blue-sidebar ${sidebarOpen ? 'open' : ''}`}></div>
            <h1 style={{ marginTop: '100px' }}>Bienvenido, {user.Nombres} {user.Apellidos}</h1>
            <MenuForm userId={user._id} /> {/* Pasar el ID del usuario como prop */}
        </div>
    );
}

export default Welcome;
